
DROP TABLE IF EXISTS `Böcker`;

CREATE TABLE `Böcker` (
  `BokID` INT PRIMARY KEY, 
  `TitelID` INT DEFAULT 0, 
  `FörfattareID` INT DEFAULT 0, 
  `SVFörlag` INT DEFAULT 0, 
  `SV1upplaga` INT DEFAULT 0, 
  `ÖversättareID` INT DEFAULT 0, 
  `RevÖversättareID` INT DEFAULT 0, 
  `IllustratörID` INT DEFAULT 0, 
  `DennaUpplaga` VARCHAR(50), 
  `AntalUpplagor` VARCHAR(50), 
  `RyggNr` VARCHAR(50), 
  `Konvelut` TINYINT(1) DEFAULT 0, 
  `Handling` CLOB
);

SET autocommit=1;


