INSERT INTO `Böcker` (`BokID`, `TitelID`, `FörfattareID`, `SVFörlag`, `SV1upplaga`, `ÖversättareID`, `RevÖversättareID`, `IllustratörID`, `DennaUpplaga`, `AntalUpplagor`, `RyggNr`, `Konvelut`, `Handling`) VALUES (1, 2, 1, 1, 1953, 8, 54, 18, '1', '1', '108', 0, 'Aldrig förr har major James Bigglesworth - bättre känd som Biggles - gjort sitt land så ovärderliga tjänster som när han fullgjorde det hemliga uppdrag under vilket han tillfälligt tilldelades eskaderchefs rang.\r\n\r\nEngelska fartyg har försvunnit i Indiska Oceanen under mystiska omständigheter. De har gått under med man och allt, men samtliga har hunnit sända en SOS - signal, vars formulering väcker Biggles’ misstankar. En närmare undersökning visar att Biggles har rätt - fartygen med krigsmaterial för Fjärran Östern har systematiskt blivit sänkta. Varför och av vem?\r\n\r\nFör att besvara dessa frågor och en gång för alla sätta stop för en tydligen fullkomligt hänsynslös motståndares framfart får Biggles oinskränkta fullmakter, och tillsammans med vännerna Ginger och Algy kastar han sig ut i ett av sitt livs mest vågsamma äventyr. Det gäller liv eller död inte bara för dem själva - hela nationens, kanske världens, väl och ve hänger på hur han löser sin ömtåliga och farofyllda uppgift.');
INSERT INTO `Böcker` (`BokID`, `TitelID`, `FörfattareID`, `SVFörlag`, `SV1upplaga`, `ÖversättareID`, `RevÖversättareID`, `IllustratörID`, `DennaUpplaga`, `AntalUpplagor`, `RyggNr`, `Konvelut`, `Handling`) VALUES (2, 2, 1, 5, 1971, 2, 54, 2, '1', '2, 1973', '1615/1616', 0, 'Flera engelska fartyg har på ett mystiskt sätt försvunnit i Indiska oceanen. Allesammans tycks ha gått under med hela sin besättning. Men innan dess hann de sända en underlig SOS-signal. Biggles blir misstänksam och börjar forska fallet. Han finner att samtliga fartyg varit lastade med krigsmateriel och att de inte gått under genom någon olyckshändelse. De har blivit sänkta!');
