INSERT INTO `Böcker` (`BokID`, `TitelID`, `FörfattareID`, `SVFörlag`, `SV1upplaga`, `ÖversättareID`, `RevÖversättareID`, `IllustratörID`, `DennaUpplaga`, `AntalUpplagor`, `RyggNr`, `Konvelut`, `Handling`) VALUES (13, 6, 1, 5, 1971, 14, 54, 2, '1', '3, 1981', '1619/1620', 0, 'En ung pilot har spårlöst försvunnit i det inre av Afrika och Biggles får i uppdrag att leta efter honom.\r\nTillsammans med sina kamrater, Ginger och Algy, beger han sig in i djungeln. Där utsätts de för många faror och tvingas slåss mot pytonormar och leoparder. Men deras farligaste motståndare är människor.');
