INSERT INTO `Böcker` (`BokID`, `TitelID`, `FörfattareID`, `SVFörlag`, `SV1upplaga`, `ÖversättareID`, `RevÖversättareID`, `IllustratörID`, `DennaUpplaga`, `AntalUpplagor`, `RyggNr`, `Konvelut`, `Handling`) VALUES (30, 14, 1, 5, 1981, 1, 54, 1, '1', '1', '2214/2215', 0, 'När Biggles nådde fram till skogsbrynet tvärstannade han och sträckte ut handen för att hejda Bertie.\r\n- Vad är det? frågade Bertie.\r\n- Titta själv!\r\nFlygplanet låg kvar där de lämnat det. Men bredvid det,  med huvudet framsträckt för att nosa på det, stod en väldig noshörning.\r\nDe hade inte en chans att ta sig fram till planet och räddningen.');
