INSERT INTO `Böcker` (`BokID`, `TitelID`, `FörfattareID`, `SVFörlag`, `SV1upplaga`, `ÖversättareID`, `RevÖversättareID`, `IllustratörID`, `DennaUpplaga`, `AntalUpplagor`, `RyggNr`, `Konvelut`, `Handling`) VALUES (32, 15, 1, 5, 1950, 24, 54, 3, '5, 1969', '7, 1987', '587/588', 0, 'En man ber Biggles att följa med till Sydpolen för att bärga en skatt som han vet finns i ett fastfruset fartyg där nere. Biggles som vill ha lite omväxling går med på det och ordnar en expidition.\r\nSå flyger de mot Falklandsöarna som är brittiskt territorium. Därifrån kan de operera.\r\nMen det är inte bara Biggles och hans män som är ute efter guldet!');
