INSERT INTO `Böcker` (`BokID`, `TitelID`, `FörfattareID`, `SVFörlag`, `SV1upplaga`, `ÖversättareID`, `RevÖversättareID`, `IllustratörID`, `DennaUpplaga`, `AntalUpplagor`, `RyggNr`, `Konvelut`, `Handling`) VALUES (40, 19, 1, 2, 1972, 26, 54, 6, '1', '1', 'x', 0, 'Handlingen tilldrar sig under finska vinterkrigets slutskede, mars 1940, då Biggles och hans kamrater Algy och Ginger var medlemmar av en internationell division, som stred på Finlands sida.\r\n\r\nMedverkande:\r\nMajor James Bigglesworth, flygareoch brittisk agent, mer känd som Biggles.\r\nAlgy Lacey, Biggles vän och vapenbroder sedan flera år.\r\nGinger Hebblethwaite, yngst i gänget.\r\nPetolski, polsk naturvetenskapsman och uppfinnare, som hinner gömma sin portfölj med viktiga anteckningar innan han dör på flykt undan ryssar och tyskar.\r\nvon  Stalhein, tysk officer och agent, som liksom Biggles är på jakt efter Petolskis revolutionerande uppfinning.\r\n\r\nDessutom medverkar sergeant Smyth, överste Raymond, chef för engelska underrättelsetjänsten, den svensk-kanadensiska spionen Olson m. fl.');
