INSERT INTO `Böcker` (`BokID`, `TitelID`, `FörfattareID`, `SVFörlag`, `SV1upplaga`, `ÖversättareID`, `RevÖversättareID`, `IllustratörID`, `DennaUpplaga`, `AntalUpplagor`, `RyggNr`, `Konvelut`, `Handling`) VALUES (61, 29, 1, 5, 1950, 40, 54, 2, '6, 1967', '9, 1987', '612/613', 0, 'Biggles blir kallad till ett sjukhus i London. Där ligger en man för döden i en svår tropisk sjukdom. Han berättar att han har hälsninar från en gammal vän till Biggles som heter Angus. Och Angus behöver hjälp. Han är nämligen fånge hos en farlig man djupt inne i Sydamerikas djungel. Biggles beslutar sig för att flyga dit, och när han kommer fram får han en chock.\r\n(från 9, 1987)');
