INSERT INTO `Böcker` (`BokID`, `TitelID`, `FörfattareID`, `SVFörlag`, `SV1upplaga`, `ÖversättareID`, `RevÖversättareID`, `IllustratörID`, `DennaUpplaga`, `AntalUpplagor`, `RyggNr`, `Konvelut`, `Handling`) VALUES (89, 1, 1, 2, 1972, 19, 54, 6, '1', '1', 'x', 0, 'Handlingen utspelas i England, Frankrike och Tyskland, där Biggles och hans trogna kompisar Algy och Ginger möter en motståndare som inte skyr några medel.\r\n\r\nMedverkande:\r\nMajor James Bigglesworth, av sina vänner kallad Biggles, som får i uppdrag av guldmäklarfirman Cronfelt & Carstairs att starta en flyglinje för de stora guldtransporterna till Frankrike. \r\nAlgy Lacey, Biggles'' gamla skvadronkamrat som försvinner under en av transporterna.\r\nGinger Hebblethwaite, Biggles'' bästa elev, utan vars hjälp Biggles och Algy hade varit förlorade.\r\nErich von Stahlhein, f.d. kapten i kejserliga armén och f.d. tjänsteman i underrättelseväsendet.\r\n\r\nDessutom medverkar mäklarna Cronfelt & Carstairs, den senares dotter Stella Carstairs, överste Raymond m.fl.');
INSERT INTO `Böcker` (`BokID`, `TitelID`, `FörfattareID`, `SVFörlag`, `SV1upplaga`, `ÖversättareID`, `RevÖversättareID`, `IllustratörID`, `DennaUpplaga`, `AntalUpplagor`, `RyggNr`, `Konvelut`, `Handling`) VALUES (90, 1, 1, 3, 1981, 19, 54, 9, '1', '1', 'x', 0, '- Skulle ni våga organisera en flyglinje? Frågan ställdes till Biggles av guldmäklaren Paul Cronfelt. Ni får fullmakt att skaffa allt ni anser er behöva och alla räkningar skickar ni bara till oss.\r\n- Jag har aldrig försökt, medgav Biggles. Men jag skulle inte bli förvånad om jag kunde det.\r\nBiggles och hans kompanjoner antar utmaningen att flyga guld och diamanter från England till Frankrike. Men inte anade Biggles att dessa flygningar skulle bli de äventyrligaste under hela hans bana.');
