INSERT INTO `Böcker` (`BokID`, `TitelID`, `FörfattareID`, `SVFörlag`, `SV1upplaga`, `ÖversättareID`, `RevÖversättareID`, `IllustratörID`, `DennaUpplaga`, `AntalUpplagor`, `RyggNr`, `Konvelut`, `Handling`) VALUES (104, 55, 1, 6, 1971, 2, 54, 2, '1', '1', '79', 0, 'Mystiska och okända flygplan har observerats vid Elfenbenskusten. Varifrån kommer de och vad har de för ärende? Finns det ett hemligt flygfölt inne i djungeln? Naturligtvis blir det Biggles som får undersöka saken. Och han ger sig ut på flygspaningar som leder honom till Afrikas mörkaste och farligaste trakter!');
