INSERT INTO `Böcker` (`BokID`, `TitelID`, `FörfattareID`, `SVFörlag`, `SV1upplaga`, `ÖversättareID`, `RevÖversättareID`, `IllustratörID`, `DennaUpplaga`, `AntalUpplagor`, `RyggNr`, `Konvelut`, `Handling`) VALUES (144, 84, 1, 6, 1970, 2, 54, 2, '1', '1', '47', 0, 'Under en flygtur i sin Camel får Biggles en brevduva mitt i ansiktet. Kring brevduvans ben sitter en liten hylsa som innehåller ett brev med en hemlig kod!\r\nSå börjar den spännande berättelsen om hur Biggles lyckas rädda en engelsk spion från en säker död…');
