INSERT INTO `Böcker` (`BokID`, `TitelID`, `FörfattareID`, `SVFörlag`, `SV1upplaga`, `ÖversättareID`, `RevÖversättareID`, `IllustratörID`, `DennaUpplaga`, `AntalUpplagor`, `RyggNr`, `Konvelut`, `Handling`) VALUES (151, 7, 1, 5, 1948, 16, 54, 2, '8, 1979', '8, 1979', '519', 0, 'Biggles flög vidare strax ovanför trädtopparna, ibland till och med mellan dem. Sedan dånade de fram över ängar och sädesfält.\r\nPeter började förstå varför flygarna brukade kalla den här sortens flygning för\r\nhinderlöpning - men med flygplan istället för häst.');
